// Process data and prepare email
const bitcoinData = $json;
const price = bitcoinData.bitcoin?.usd || 'N/A';
const timestamp = new Date().toLocaleString();

// Use parameters from webhook body
const webhookData = $node["Webhook"].json.body;
const fromEmail = webhookData.fromEmail || 'noreply@example.com';
const toEmail = webhookData.toEmail || 'user@example.com';
const subject = webhookData.subject || `Bitcoin Price: $${price}`;

console.log('üìß From Email:', fromEmail);
console.log('üìß To Email:', toEmail);
console.log('üí∞ Bitcoin Price:', price);

if (price > 50000) {
  return [{
    json: {
      fromEmail: fromEmail,
      toEmail: toEmail,
      subject: "üö® BITCOIN PRICE ALERT - $" + price,
      message: "<h2>Bitcoin Price Alert</h2><p>Bitcoin price is currently <strong>HIGH</strong>!</p><ul><li>üí∞ Price: $" + price + "</li><li>üìà Status: ABOVE $50,000</li><li>üïê Time: " + timestamp + "</li></ul><p>Consider monitoring the market closely.</p>",
      emailFormat: "html",
      status: "high_alert",
      price: price,
      timestamp: timestamp
    }
  }];
} else {
  return [{
    json: {
      fromEmail: fromEmail,
      toEmail: toEmail,
      subject: "üìä Bitcoin Price Update - $" + price,
      message: "<h2>Bitcoin Price Update</h2><p>Bitcoin price is within normal range.</p><ul><li>üí∞ Price: $" + price + "</li><li>üìä Status: BELOW $50,000</li><li>üïê Time: " + timestamp + "</li></ul><p>No immediate action needed.</p>",
      emailFormat: "html",
      status: "normal",
      price: price,
      timestamp: timestamp
    }
  }];
}
