// Get data from webhook trigger
const webhookData = $input.first().json;
const fromEmail = webhookData.fromEmail || 'noreply@example.com';
const toEmail = webhookData.toEmail || 'user@example.com';

// Get Bitcoin price from previous node
const bitcoinData = $('Get Bitcoin Price').first().json;
const price = bitcoinData.bitcoin.usd;
const timestamp = new Date().toLocaleString();

console.log('?? From Email:', fromEmail);
console.log('?? To Email:', toEmail);
console.log('?? Bitcoin Price:', price);

if (price > 50000) {
  return {
    json: {
      fromEmail: fromEmail,
      toEmail: toEmail,
      subject: '?? BITCOIN PRICE ALERT - $' + price,
      message: '<h2>Bitcoin Price Alert</h2><p>Bitcoin price is currently <strong>HIGH</strong>!</p><ul><li>?? Price: $' + price + '</li><li>?? Status: ABOVE $50,000</li><li>? Time: ' + timestamp + '</li></ul><p>Consider monitoring the market closely.</p>',
      emailFormat: 'html',
      status: 'high_alert',
      price: price,
      timestamp: timestamp
    }
  };
} else {
  return {
    json: {
      fromEmail: fromEmail,
      toEmail: toEmail,
      subject: '?? Bitcoin Price Update - $' + price,
      message: '<h2>Bitcoin Price Update</h2><p>Bitcoin price is within normal range.</p><ul><li>?? Price: $' + price + '</li><li>?? Status: BELOW $50,000</li><li>? Time: ' + timestamp + '</li></ul><p>No immediate action needed.</p>',
      emailFormat: 'html',
      status: 'normal',
      price: price,
      timestamp: timestamp
    }
  };
}
